  #earth {
  position: absolute;
  width: 950px;
  padding-top: 950px;
  left: 50%;
  margin-left: -475px;
  top:3%;
  background: url(01a8a783d402046fee289794ab2dcfaa.jpg) 0 0 repeat;
  border-radius: 50%;
  background-size: 1900px;
  animation: rotate 120s linear infinite;
  transform-style: preserve-3d;
  box-shadow: inset 60px 0 230px 18px rgba(0, 0, 0, 1);
}

#earth:after {
  content: "";
  position: absolute;
  top: 0%;
  left: 0%;
  width: 100%;
  height: 100%;
  border-radius: 50%;
  box-shadow: -220px 45px 230px 35px rgba(0,0,0,.9) inset;
}

#earth:before {
  content: "";
  position: absolute;
  border-radius: 50%;
  width: 100%;
  height: 100%;
  top: 0;
  left: 0;
  opacity: 0.2;
  background: radial-gradient(circle at 300px 300px, #fff, #000);
}

@keyframes rotate {
  0% {background-position: 0 0;}
  100% {background-position: 1900px 0;}
}
